import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertProductSchema, insertOrderSchema, insertOrderItemSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Categories routes
  app.get('/api/categories', async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  // Products routes
  app.get('/api/products', async (req, res) => {
    try {
      const { categoryId } = req.query;
      const products = await storage.getAvailableProducts(categoryId as string);
      res.json(products);
    } catch (error) {
      console.error("Error fetching products:", error);
      res.status(500).json({ message: "Failed to fetch products" });
    }
  });

  app.get('/api/products/:id', async (req, res) => {
    try {
      const product = await storage.getProductById(req.params.id);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      console.error("Error fetching product:", error);
      res.status(500).json({ message: "Failed to fetch product" });
    }
  });

  app.post('/api/products', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (user?.role !== 'manufacturer') {
        return res.status(403).json({ message: "Only manufacturers can create products" });
      }

      const productData = insertProductSchema.parse({
        ...req.body,
        manufacturerId: userId,
      });

      const product = await storage.createProduct(productData);
      res.status(201).json(product);
    } catch (error) {
      console.error("Error creating product:", error);
      res.status(500).json({ message: "Failed to create product" });
    }
  });

  // Orders routes
  app.get('/api/orders', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      let orders;
      if (user?.role === 'retailer') {
        orders = await storage.getOrdersByRetailer(userId);
      } else if (user?.role === 'distributor') {
        orders = await storage.getOrdersByDistributor(userId);
      } else {
        return res.status(403).json({ message: "Access denied" });
      }

      res.json(orders);
    } catch (error) {
      console.error("Error fetching orders:", error);
      res.status(500).json({ message: "Failed to fetch orders" });
    }
  });

  app.get('/api/orders/:id', isAuthenticated, async (req: any, res) => {
    try {
      const order = await storage.getOrderById(req.params.id);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }

      const orderItems = await storage.getOrderItems(order.id);
      res.json({ ...order, items: orderItems });
    } catch (error) {
      console.error("Error fetching order:", error);
      res.status(500).json({ message: "Failed to fetch order" });
    }
  });

  const createOrderSchema = z.object({
    distributorId: z.string(),
    items: z.array(z.object({
      productId: z.string(),
      quantity: z.number().positive(),
      unitPrice: z.string(),
    })),
    notes: z.string().optional(),
    deliveryMode: z.enum(['pickup', 'delivery']).optional(),
  });

  app.post('/api/orders', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (user?.role !== 'retailer') {
        return res.status(403).json({ message: "Only retailers can create orders" });
      }

      const { distributorId, items, notes, deliveryMode } = createOrderSchema.parse(req.body);

      // Calculate total amount
      const totalAmount = items.reduce((sum, item) => 
        sum + (parseFloat(item.unitPrice) * item.quantity), 0
      );

      // Generate order number
      const orderNumber = `ORD-${new Date().getFullYear()}-${Date.now()}`;

      // Create order
      const order = await storage.createOrder({
        orderNumber,
        retailerId: userId,
        distributorId,
        totalAmount: totalAmount.toString(),
        notes,
        deliveryMode: deliveryMode || 'delivery',
        status: 'pending',
      });

      // Create order items
      const orderItemsData = items.map(item => ({
        orderId: order.id,
        productId: item.productId,
        quantity: item.quantity,
        unitPrice: item.unitPrice,
        totalPrice: (parseFloat(item.unitPrice) * item.quantity).toString(),
      }));

      await storage.createOrderItems(orderItemsData);

      // Create WhatsApp notification for distributor
      await storage.createNotification({
        userId: distributorId,
        message: `New order ${orderNumber} from ${user.businessName || user.firstName}`,
        type: 'order_placed',
        sentAt: null,
        isDelivered: false,
      });

      res.status(201).json(order);
    } catch (error) {
      console.error("Error creating order:", error);
      res.status(500).json({ message: "Failed to create order" });
    }
  });

  app.patch('/api/orders/:id/status', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (user?.role !== 'distributor') {
        return res.status(403).json({ message: "Only distributors can update order status" });
      }

      const { status } = z.object({ status: z.string() }).parse(req.body);
      const order = await storage.updateOrderStatus(req.params.id, status);

      // Create WhatsApp notification for retailer
      await storage.createNotification({
        userId: order.retailerId,
        message: `Order ${order.orderNumber} status updated to: ${status}`,
        type: 'order_status_update',
        sentAt: null,
        isDelivered: false,
      });

      res.json(order);
    } catch (error) {
      console.error("Error updating order status:", error);
      res.status(500).json({ message: "Failed to update order status" });
    }
  });

  // Analytics routes
  app.get('/api/analytics/stats', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const stats = await storage.getOrderStats(userId, user.role);

      // Add role-specific stats
      if (user.role === 'retailer') {
        const distributors = await storage.getDistributors();
        stats.activeDistributors = distributors.length;
      }

      res.json(stats);
    } catch (error) {
      console.error("Error fetching analytics:", error);
      res.status(500).json({ message: "Failed to fetch analytics" });
    }
  });

  // Distributors routes
  app.get('/api/distributors', async (req, res) => {
    try {
      const distributors = await storage.getDistributors();
      res.json(distributors);
    } catch (error) {
      console.error("Error fetching distributors:", error);
      res.status(500).json({ message: "Failed to fetch distributors" });
    }
  });

  // Inventory routes (for distributors)
  app.get('/api/inventory', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (user?.role !== 'distributor') {
        return res.status(403).json({ message: "Only distributors can view inventory" });
      }

      const inventory = await storage.getInventoryByDistributor(userId);
      res.json(inventory);
    } catch (error) {
      console.error("Error fetching inventory:", error);
      res.status(500).json({ message: "Failed to fetch inventory" });
    }
  });

  // WhatsApp notifications routes
  app.get('/api/notifications', isAuthenticated, async (req: any, res) => {
    try {
      const notifications = await storage.getPendingNotifications();
      res.json(notifications);
    } catch (error) {
      console.error("Error fetching notifications:", error);
      res.status(500).json({ message: "Failed to fetch notifications" });
    }
  });

  // Partnership routes
  app.get("/api/partnerships", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const partnerships = await storage.getPartnerships(userId);
      res.json(partnerships);
    } catch (error) {
      console.error("Error fetching partnerships:", error);
      res.status(500).json({ message: "Failed to fetch partnerships" });
    }
  });

  app.get("/api/partners/available", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      const availablePartners = await storage.getAvailablePartners(userId, user.role);
      res.json(availablePartners);
    } catch (error) {
      console.error("Error fetching available partners:", error);
      res.status(500).json({ message: "Failed to fetch available partners" });
    }
  });

  app.post("/api/partnerships/request", isAuthenticated, async (req: any, res) => {
    try {
      const requesterId = req.user.claims.sub;
      const { partnerId, partnershipType } = req.body;

      if (!partnerId || !partnershipType) {
        return res.status(400).json({ message: "Partner ID and partnership type are required" });
      }

      const partnership = await storage.sendPartnershipRequest(requesterId, partnerId, partnershipType);
      res.json(partnership);
    } catch (error) {
      console.error("Error sending partnership request:", error);
      res.status(500).json({ message: "Failed to send partnership request" });
    }
  });

  app.patch("/api/partnerships/:id/respond", isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const { status } = req.body;

      if (!['approved', 'rejected'].includes(status)) {
        return res.status(400).json({ message: "Invalid status" });
      }

      const partnership = await storage.respondToPartnershipRequest(id, status);
      res.json(partnership);
    } catch (error) {
      console.error("Error responding to partnership request:", error);
      res.status(500).json({ message: "Failed to respond to partnership request" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  categories,
  products,
  inventory,
  orders,
  orderItems,
  invoices,
  whatsappNotifications,
  partnerships,
  type User,
  type UpsertUser,
  type Category,
  type InsertCategory,
  type Product,
  type InsertProduct,
  type Inventory,
  type InsertInventory,
  type Order,
  type InsertOrder,
  type OrderItem,
  type InsertOrderItem,
  type Invoice,
  type InsertInvoice,
  type WhatsappNotification,
  type Partnership,
  type InsertPartnership,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, sql, or } from "drizzle-orm";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Category operations
  getCategories(): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Product operations
  getProducts(categoryId?: string): Promise<Product[]>;
  getProductById(id: string): Promise<Product | undefined>;
  createProduct(product: InsertProduct): Promise<Product>;

  // Inventory operations
  getInventoryByDistributor(distributorId: string): Promise<Inventory[]>;
  getAvailableProducts(categoryId?: string): Promise<(Product & { inventory: Inventory })[]>;
  updateInventory(inventoryData: InsertInventory): Promise<Inventory>;

  // Order operations
  createOrder(order: InsertOrder): Promise<Order>;
  getOrdersByRetailer(retailerId: string): Promise<Order[]>;
  getOrdersByDistributor(distributorId: string): Promise<Order[]>;
  getOrderById(id: string): Promise<Order | undefined>;
  updateOrderStatus(orderId: string, status: string): Promise<Order>;

  // Order item operations
  createOrderItems(items: InsertOrderItem[]): Promise<OrderItem[]>;
  getOrderItems(orderId: string): Promise<(OrderItem & { product: Product })[]>;

  // Invoice operations
  createInvoice(invoice: InsertInvoice): Promise<Invoice>;
  getInvoiceByOrderId(orderId: string): Promise<Invoice | undefined>;

  // WhatsApp notification operations
  createNotification(notification: Omit<WhatsappNotification, 'id' | 'createdAt'>): Promise<WhatsappNotification>;
  getPendingNotifications(): Promise<WhatsappNotification[]>;
  markNotificationSent(id: string): Promise<void>;

  // Analytics operations
  getOrderStats(userId: string, role: string): Promise<any>;
  getDistributors(): Promise<User[]>;
  getManufacturers(): Promise<User[]>;

  // Partnership operations
  getPartnerships(userId: string): Promise<(Partnership & { partner: User })[]>;
  getAvailablePartners(userId: string, userRole: string): Promise<User[]>;
  sendPartnershipRequest(requesterId: string, partnerId: string, partnershipType: string): Promise<Partnership>;
  respondToPartnershipRequest(partnershipId: string, status: 'approved' | 'rejected'): Promise<Partnership>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return await db.select().from(categories);
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db.insert(categories).values(category).returning();
    return newCategory;
  }

  // Product operations
  async getProducts(categoryId?: string): Promise<Product[]> {
    if (categoryId) {
      return await db.select().from(products).where(and(
        eq(products.isActive, true),
        eq(products.categoryId, categoryId)
      ));
    }
    return await db.select().from(products).where(eq(products.isActive, true));
  }

  async getProductById(id: string): Promise<Product | undefined> {
    const [product] = await db.select().from(products).where(eq(products.id, id));
    return product;
  }

  async createProduct(product: InsertProduct): Promise<Product> {
    const [newProduct] = await db.insert(products).values(product).returning();
    return newProduct;
  }

  // Inventory operations
  async getInventoryByDistributor(distributorId: string): Promise<Inventory[]> {
    return await db
      .select()
      .from(inventory)
      .where(eq(inventory.distributorId, distributorId));
  }

  async getAvailableProducts(categoryId?: string): Promise<(Product & { inventory: Inventory })[]> {
    let whereConditions = and(
      eq(products.isActive, true),
      eq(inventory.isAvailable, true),
      sql`${inventory.quantity} > 0`
    );

    if (categoryId) {
      whereConditions = and(
        whereConditions,
        eq(products.categoryId, categoryId)
      );
    }

    const results = await db
      .select()
      .from(products)
      .innerJoin(inventory, eq(products.id, inventory.productId))
      .where(whereConditions);

    return results.map(result => ({
      ...result.products,
      inventory: result.inventory
    }));
  }

  async updateInventory(inventoryData: InsertInventory): Promise<Inventory> {
    const [updatedInventory] = await db
      .insert(inventory)
      .values(inventoryData)
      .onConflictDoUpdate({
        target: [inventory.distributorId, inventory.productId],
        set: {
          ...inventoryData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return updatedInventory;
  }

  // Order operations
  async createOrder(order: InsertOrder): Promise<Order> {
    const [newOrder] = await db.insert(orders).values(order).returning();
    return newOrder;
  }

  async getOrdersByRetailer(retailerId: string): Promise<Order[]> {
    return await db
      .select()
      .from(orders)
      .where(eq(orders.retailerId, retailerId))
      .orderBy(desc(orders.createdAt));
  }

  async getOrdersByDistributor(distributorId: string): Promise<Order[]> {
    return await db
      .select()
      .from(orders)
      .where(eq(orders.distributorId, distributorId))
      .orderBy(desc(orders.createdAt));
  }

  async getOrderById(id: string): Promise<Order | undefined> {
    const [order] = await db.select().from(orders).where(eq(orders.id, id));
    return order;
  }

  async updateOrderStatus(orderId: string, status: string): Promise<Order> {
    const [updatedOrder] = await db
      .update(orders)
      .set({ status: status as any, updatedAt: new Date() })
      .where(eq(orders.id, orderId))
      .returning();
    return updatedOrder;
  }

  // Order item operations
  async createOrderItems(items: InsertOrderItem[]): Promise<OrderItem[]> {
    return await db.insert(orderItems).values(items).returning();
  }

  async getOrderItems(orderId: string): Promise<(OrderItem & { product: Product })[]> {
    const results = await db
      .select()
      .from(orderItems)
      .innerJoin(products, eq(orderItems.productId, products.id))
      .where(eq(orderItems.orderId, orderId));

    return results.map(result => ({
      ...result.order_items,
      product: result.products
    }));
  }

  // Invoice operations
  async createInvoice(invoice: InsertInvoice): Promise<Invoice> {
    const [newInvoice] = await db.insert(invoices).values(invoice).returning();
    return newInvoice;
  }

  async getInvoiceByOrderId(orderId: string): Promise<Invoice | undefined> {
    const [invoice] = await db.select().from(invoices).where(eq(invoices.orderId, orderId));
    return invoice;
  }

  // WhatsApp notification operations
  async createNotification(notification: Omit<WhatsappNotification, 'id' | 'createdAt'>): Promise<WhatsappNotification> {
    const [newNotification] = await db.insert(whatsappNotifications).values(notification).returning();
    return newNotification;
  }

  async getPendingNotifications(): Promise<WhatsappNotification[]> {
    return await db
      .select()
      .from(whatsappNotifications)
      .where(eq(whatsappNotifications.isDelivered, false));
  }

  async markNotificationSent(id: string): Promise<void> {
    await db
      .update(whatsappNotifications)
      .set({ isDelivered: true, sentAt: new Date() })
      .where(eq(whatsappNotifications.id, id));
  }

  // Analytics operations
  async getOrderStats(userId: string, role: string): Promise<any> {
    const whereClause = role === 'retailer' 
      ? eq(orders.retailerId, userId)
      : eq(orders.distributorId, userId);

    const totalOrders = await db
      .select({ count: sql<number>`count(*)` })
      .from(orders)
      .where(whereClause);

    const pendingOrders = await db
      .select({ count: sql<number>`count(*)` })
      .from(orders)
      .where(and(whereClause, eq(orders.status, 'pending')));

    const monthlySpend = await db
      .select({ total: sql<number>`sum(${orders.totalAmount})` })
      .from(orders)
      .where(and(
        whereClause,
        sql`${orders.createdAt} >= date_trunc('month', current_date)`
      ));

    return {
      totalOrders: totalOrders[0]?.count || 0,
      pendingOrders: pendingOrders[0]?.count || 0,
      monthlySpend: monthlySpend[0]?.total || 0,
    };
  }

  async getDistributors(): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .where(and(eq(users.role, 'distributor'), eq(users.isActive, true)));
  }

  async getManufacturers(): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .where(and(eq(users.role, 'manufacturer'), eq(users.isActive, true)));
  }

  // Partnership operations
  async getPartnerships(userId: string): Promise<(Partnership & { partner: User })[]> {
    const results = await db
      .select()
      .from(partnerships)
      .innerJoin(users, eq(partnerships.partnerId, users.id))
      .where(and(
        eq(partnerships.requesterId, userId),
        eq(partnerships.status, 'approved')
      ));

    return results.map(result => ({
      ...result.partnerships,
      partner: result.users
    }));
  }

  async getAvailablePartners(userId: string, userRole: string): Promise<User[]> {
    // Business logic: 
    // - Retailers can only see distributors
    // - Distributors can see both retailers and manufacturers
    // - Manufacturers can only see distributors
    // - Exclude users who are already partners or have pending requests

    let allowedRoles: string[] = [];
    switch (userRole) {
      case 'retailer':
        allowedRoles = ['distributor'];
        break;
      case 'distributor':
        allowedRoles = ['retailer', 'manufacturer'];
        break;
      case 'manufacturer':
        allowedRoles = ['distributor'];
        break;
      default:
        return [];
    }

    // Get existing partnership connections
    const existingPartnerships = await db
      .select({ partnerId: partnerships.partnerId })
      .from(partnerships)
      .where(eq(partnerships.requesterId, userId));

    const existingPartnerIds = existingPartnerships.map(p => p.partnerId);

    // Get available partners excluding existing ones
    const availablePartners = await db
      .select()
      .from(users)
      .where(and(
        sql`${users.role} = ANY(${allowedRoles})`,
        eq(users.isActive, true),
        sql`${users.id} != ${userId}`,
        existingPartnerIds.length > 0 
          ? sql`${users.id} NOT IN (${sql.join(existingPartnerIds.map(id => sql`${id}`), sql`, `)})`
          : sql`1=1`
      ));

    return availablePartners;
  }

  async sendPartnershipRequest(requesterId: string, partnerId: string, partnershipType: string): Promise<Partnership> {
    const [partnership] = await db
      .insert(partnerships)
      .values({
        requesterId,
        partnerId,
        partnershipType: partnershipType as "supplier" | "distributor" | "retailer",
        status: 'pending'
      })
      .returning();

    return partnership;
  }

  async respondToPartnershipRequest(partnershipId: string, status: 'approved' | 'rejected'): Promise<Partnership> {
    const [partnership] = await db
      .update(partnerships)
      .set({ 
        status, 
        updatedAt: new Date() 
      })
      .where(eq(partnerships.id, partnershipId))
      .returning();

    return partnership;
  }
}

export const storage = new DatabaseStorage();

