import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import Header from "@/components/layout/header";
import MobileNav from "@/components/layout/mobile-nav";
import OrderStatus from "@/components/orders/order-status"; 
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Package, Download, MessageCircle } from "lucide-react";
import { useEffect } from "react";
import { useToast } from "@/hooks/use-toast";

export default function Orders() {
  const { user, isLoading } = useAuth();
  const { toast } = useToast();

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !user) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [user, isLoading, toast]);

  const { data: orders, isLoading: ordersLoading } = useQuery({
    queryKey: ["/api/orders"],
    enabled: !!user,
  });

  const getStatusBadgeClass = (status: string) => {
    switch (status) {
      case 'pending':
        return 'status-pending';
      case 'confirmed':
        return 'status-confirmed';
      case 'packed':
        return 'status-packed';
      case 'out_for_delivery':
        return 'status-out-for-delivery';
      case 'delivered':
        return 'status-delivered';
      case 'cancelled':
        return 'status-cancelled';
      default:
        return 'status-pending';
    }
  };

  if (isLoading || !user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading orders...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-gray-900" data-testid="text-page-title">
            Order Management
          </h1>
          <p className="text-gray-600 mt-1">
            {user.role === 'retailer' ? 'Track your orders and download invoices' : 'Manage and process incoming orders'}
          </p>
        </div>

        {ordersLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[1, 2, 3].map((i) => (
              <Card key={i} className="animate-pulse">
                <CardHeader>
                  <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                  <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2">
                    <div className="h-3 bg-gray-200 rounded"></div>
                    <div className="h-3 bg-gray-200 rounded w-2/3"></div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : orders && orders.length > 0 ? (
          <div className="space-y-6">
            {orders.map((order: any) => (
              <Card key={order.id} className="hover:shadow-lg transition-shadow">
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <div>
                      <CardTitle className="flex items-center gap-2" data-testid={`text-order-number-${order.id}`}>
                        <Package className="h-5 w-5" />
                        {order.orderNumber}
                      </CardTitle>
                      <p className="text-sm text-gray-600 mt-1">
                        {new Date(order.createdAt).toLocaleDateString('en-IN', {
                          year: 'numeric',
                          month: 'long', 
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </p>
                    </div>
                    <Badge className={getStatusBadgeClass(order.status)} data-testid={`badge-order-status-${order.id}`}>
                      {order.status.replace('_', ' ').toUpperCase()}
                    </Badge>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <h4 className="font-semibold text-gray-900 mb-2">Order Details</h4>
                      <div className="space-y-1 text-sm text-gray-600">
                        <p><strong>Total Amount:</strong> â‚¹{order.totalAmount}</p>
                        <p><strong>Delivery Mode:</strong> {order.deliveryMode === 'pickup' ? 'Self Pickup' : 'Home Delivery'}</p>
                        {order.notes && <p><strong>Notes:</strong> {order.notes}</p>}
                      </div>
                    </div>
                    <div>
                      <h4 className="font-semibold text-gray-900 mb-2">Order Status</h4>
                      <OrderStatus status={order.status} />
                    </div>
                  </div>

                  <div className="flex flex-wrap gap-2 mt-6">
                    <Button variant="outline" size="sm" data-testid={`button-view-details-${order.id}`}>
                      View Details
                    </Button>
                    <Button variant="outline" size="sm" data-testid={`button-download-invoice-${order.id}`}>
                      <Download className="h-4 w-4 mr-1" />
                      Invoice
                    </Button>
                    <Button variant="outline" size="sm" className="whatsapp-green text-white hover:bg-green-600" data-testid={`button-whatsapp-${order.id}`}>
                      <MessageCircle className="h-4 w-4 mr-1" />
                      WhatsApp
                    </Button>
                    {user.role === 'distributor' && order.status === 'pending' && (
                      <>
                        <Button size="sm" className="action-button-secondary" data-testid={`button-approve-${order.id}`}>
                          Approve
                        </Button>
                        <Button size="sm" variant="destructive" data-testid={`button-reject-${order.id}`}>
                          Reject
                        </Button>
                      </>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <Card>
            <CardContent className="text-center py-12">
              <Package className="h-16 w-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">No Orders Found</h3>
              <p className="text-gray-600 mb-6">
                {user.role === 'retailer' 
                  ? "You haven't placed any orders yet. Browse products to get started."
                  : "No orders to process at the moment."
                }
              </p>
              {user.role === 'retailer' && (
                <Button className="action-button-primary" data-testid="button-browse-products">
                  Browse Products
                </Button>
              )}
            </CardContent>
          </Card>
        )}
      </div>

      <MobileNav />
    </div>
  );
}

client/src/pages/partnerships.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Users, Plus, Search, CheckCircle, XCircle, Clock, Building2 } from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";

interface User {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  role: string;
  companyName: string;
}

interface Partnership {
  id: string;
  requesterId: string;
  partnerId: string;
  status: string;
  partnershipType: string;
  createdAt: string;
  partner: User;
}

export default function Partnerships() {
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedPartnershipType, setSelectedPartnershipType] = useState<string>("");
  const [isAddPartnerOpen, setIsAddPartnerOpen] = useState(false);

  // Fetch current partnerships
  const { data: partnerships = [], isLoading: partnershipsLoading } = useQuery<Partnership[]>({
    queryKey: ["/api/partnerships"],
    enabled: isAuthenticated,
  });

  // Fetch available partners to connect with
  const { data: availablePartners = [], isLoading: partnersLoading } = useQuery<User[]>({
    queryKey: ["/api/partners/available"],
    enabled: isAuthenticated,
  });

  // Send partnership request mutation
  const sendRequestMutation = useMutation({
    mutationFn: async ({ partnerId, partnershipType }: { partnerId: string; partnershipType: string }) => {
      await apiRequest("/api/partnerships/request", "POST", { partnerId, partnershipType });
    },
    onSuccess: () => {
      toast({
        title: "Partnership Request Sent",
        description: "Your partnership request has been sent successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/partnerships"] });
      queryClient.invalidateQueries({ queryKey: ["/api/partners/available"] });
      setIsAddPartnerOpen(false);
      setSelectedPartnershipType("");
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to send partnership request. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendRequest = (partnerId: string) => {
    if (!selectedPartnershipType) {
      toast({
        title: "Select Partnership Type",
        description: "Please select a partnership type before sending the request.",
        variant: "destructive",
      });
      return;
    }
    sendRequestMutation.mutate({ partnerId, partnershipType: selectedPartnershipType });
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "approved":
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case "rejected":
        return <XCircle className="h-4 w-4 text-red-600" />;
      default:
        return <Clock className="h-4 w-4 text-yellow-600" />;
    }
  };

  const getStatusBadge = (status: string) => {
    const variants = {
      approved: "default",
      rejected: "destructive",
      pending: "secondary",
    } as const;

    return (
      <Badge variant={variants[status as keyof typeof variants] || "secondary"}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </Badge>
    );
  };

  const getRoleDescription = (role: string) => {
    switch (role) {
      case "retailer":
        return "Retail business looking for suppliers";
      case "distributor":
        return "Distribution company connecting manufacturers and retailers";
      case "manufacturer":
        return "Manufacturing company producing goods";
      default:
        return "Business partner";
    }
  };

  const filteredPartners = availablePartners.filter((partner) => 
    partner.companyName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    partner.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    `${partner.firstName} ${partner.lastName}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800">
      <div className="container mx-auto px-4 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
            Business Partnerships
          </h1>
          <p className="text-gray-600 dark:text-gray-300">
            Manage your business connections and partnerships to streamline your supply chain
          </p>
        </div>

        <div className="grid gap-6 md:grid-cols-2">
          {/* Current Partnerships */}
          <Card data-testid="card-current-partnerships">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Users className="h-5 w-5" />
                Current Partnerships ({partnerships.length})
              </CardTitle>
              <CardDescription>
                Your active business partnerships and connection requests
              </CardDescription>
            </CardHeader>
            <CardContent>
              {partnershipsLoading ? (
                <div className="space-y-4">
                  {[1, 2, 3].map((i) => (
                    <div key={i} className="animate-pulse bg-gray-200 dark:bg-gray-700 h-16 rounded" />
                  ))}
                </div>
              ) : partnerships.length === 0 ? (
                <div className="text-center py-8">
                  <Building2 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-500 dark:text-gray-400">No partnerships yet</p>
                  <p className="text-sm text-gray-400 dark:text-gray-500">
                    Start building your network by adding business partners
                  </p>
                </div>
              ) : (
                <div className="space-y-4">
                  {partnerships.map((partnership) => (
                    <div
                      key={partnership.id}
                      className="flex items-center justify-between p-4 border rounded-lg bg-white dark:bg-gray-800"
                      data-testid={`card-partnership-${partnership.id}`}
                    >
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <h3 className="font-medium text-gray-900 dark:text-white">
                            {partnership.partner.companyName || 
                             `${partnership.partner.firstName} ${partnership.partner.lastName}`}
                          </h3>
                          {getStatusIcon(partnership.status)}
                        </div>
                        <p className="text-sm text-gray-600 dark:text-gray-300">
                          {getRoleDescription(partnership.partner.role)}
                        </p>
                        <p className="text-xs text-gray-500 dark:text-gray-400">
                          {partnership.partner.email}
                        </p>
                      </div>
                      <div className="flex flex-col items-end gap-2">
                        {getStatusBadge(partnership.status)}
                        <span className="text-xs text-gray-500 dark:text-gray-400">
                          {partnership.partnershipType}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Add New Partnership */}
          <Card data-testid="card-add-partnership">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Plus className="h-5 w-5" />
                Find New Partners
              </CardTitle>
              <CardDescription>
                Discover and connect with potential business partners
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                  <Input
                    placeholder="Search partners by company or name..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                    data-testid="input-search-partners"
                  />
                </div>

                {partnersLoading ? (
                  <div className="space-y-4">
                    {[1, 2, 3].map((i) => (
                      <div key={i} className="animate-pulse bg-gray-200 dark:bg-gray-700 h-16 rounded" />
                    ))}
                  </div>
                ) : filteredPartners.length === 0 ? (
                  <div className="text-center py-8">
                    <Building2 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500 dark:text-gray-400">
                      {searchTerm ? "No partners found" : "No available partners"}
                    </p>
                    <p className="text-sm text-gray-400 dark:text-gray-500">
                      {searchTerm ? "Try adjusting your search terms" : "All potential partners are already connected"}
                    </p>
                  </div>
                ) : (
                  <div className="space-y-4 max-h-96 overflow-y-auto">
                    {filteredPartners.map((partner) => (
                      <div
                        key={partner.id}
                        className="flex items-center justify-between p-4 border rounded-lg bg-white dark:bg-gray-800"
                        data-testid={`card-available-partner-${partner.id}`}
                      >
                        <div className="flex-1">
                          <h3 className="font-medium text-gray-900 dark:text-white mb-1">
                            {partner.companyName || `${partner.firstName} ${partner.lastName}`}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-300">
                            {getRoleDescription(partner.role)}
                          </p>
                          <p className="text-xs text-gray-500 dark:text-gray-400">
                            {partner.email}
                          </p>
                        </div>
                        <Dialog open={isAddPartnerOpen} onOpenChange={setIsAddPartnerOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              onClick={() => setIsAddPartnerOpen(true)}
                              data-testid={`button-connect-${partner.id}`}
                            >
                              Connect
                            </Button>
                          </DialogTrigger>
                          <DialogContent>
                            <DialogHeader>
                              <DialogTitle>Send Partnership Request</DialogTitle>
                              <DialogDescription>
                                Send a partnership request to {partner.companyName || `${partner.firstName} ${partner.lastName}`}
                              </DialogDescription>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <label className="text-sm font-medium">Partnership Type</label>
                                <Select 
                                  value={selectedPartnershipType} 
                                  onValueChange={setSelectedPartnershipType}
                                >
                                  <SelectTrigger data-testid="select-partnership-type">
                                    <SelectValue placeholder="Select partnership type" />
                                  </SelectTrigger>
                                  <SelectContent>
                                    <SelectItem value="supplier">Supplier Partnership</SelectItem>
                                    <SelectItem value="distributor">Distribution Partnership</SelectItem>
                                    <SelectItem value="retailer">Retail Partnership</SelectItem>
                                  </SelectContent>
                                </Select>
                              </div>
                              <div className="flex justify-end gap-2">
                                <Button 
                                  variant="outline" 
                                  onClick={() => setIsAddPartnerOpen(false)}
                                  data-testid="button-cancel-request"
                                >
                                  Cancel
                                </Button>
                                <Button 
                                  onClick={() => handleSendRequest(partner.id)}
                                  disabled={sendRequestMutation.isPending}
                                  data-testid="button-send-request"
                                >
                                  {sendRequestMutation.isPending ? "Sending..." : "Send Request"}
                                </Button>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/products.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import Header from "@/components/layout/header";
import MobileNav from "@/components/layout/mobile-nav";
import ProductGrid from "@/components/products/product-grid";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Search } from "lucide-react";
import { useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import type { User } from "@shared/schema";

export default function Products() {
  const { user, isLoading } = useAuth();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !user) {
      toast({
        title: "Unauthorized", 
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [user, isLoading, toast]);

  const { data: categories, isLoading: categoriesLoading } = useQuery({
    queryKey: ["/api/categories"],
    enabled: !!user,
  });

  const { data: products, isLoading: productsLoading } = useQuery({
    queryKey: ["/api/products", selectedCategory === "all" ? "" : selectedCategory],
    enabled: !!user,
  });

  const filteredProducts = Array.isArray(products) ? products.filter((product: any) =>
    product.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    product.description?.toLowerCase().includes(searchTerm.toLowerCase())
  ) : [];

  if (isLoading || !user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading products...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-gray-900" data-testid="text-page-title">
            Product Catalog
          </h1>
          <p className="text-gray-600 mt-1">
            Browse and order from available products
          </p>
        </div>

        {/* Search and Filter */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Search & Filter</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex flex-col md:flex-row gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="Search products..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                  data-testid="input-search-products"
                />
              </div>
              <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                <SelectTrigger className="w-full md:w-[200px]" data-testid="select-category">
                  <SelectValue placeholder="All Categories" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  {Array.isArray(categories) && categories.map((category: any) => (
                    <SelectItem key={category.id} value={category.id}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </CardContent>
        </Card>

        {/* Product Grid */}
        <ProductGrid 
          products={filteredProducts}
          isLoading={productsLoading}
          userRole={(user as User)?.role || 'retailer'}
        />
      </div>

      <MobileNav />
    </div>
  );
}

